import React from "react";
import { useState, useRef } from "react";
import axios from "axios"; // Import Axios
import Header from "../Header/Header";
import "./upload.css";
import UploadIcon from "../../assets/uploadicon.svg";

function Upload() {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const fileInputRef = useRef(null);

  const handleFileDrop = (e) => {
    e.preventDefault();
    const files = Array.from(e.dataTransfer.files);
    setSelectedFiles(files);
  };

  const handleFileSelect = (e) => {
    const files = Array.from(e.target.files);
    setSelectedFiles(files);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    selectedFiles.forEach((file) => {
      formData.append("file", file);
    });
  
    try {
      const response = await axios.post("http://127.0.0.1:8000/filestore/upload_invoice/", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      console.log(response.data);
      if (response.status === 200) {
        alert("Files uploaded successfully!");
      }
    } catch (error) {
      console.error("Error uploading files: ", error);
    }
  };

  const handleAreaClick = () => {
    fileInputRef.current.click();
  };

  return (
    <div>
      <Header />
      <div className="container">
        <div class="upload-text">
          <h1>Upload Your Invoices Here...</h1>
        </div>
        <div
          className="drop-area"
          onClick={handleAreaClick}
          onDragOver={(e) => e.preventDefault()}
          onDrop={handleFileDrop}
        >
          <img src={UploadIcon} alt="file icon" className="file-icon" />
          <p className="file-text">Drop files here or click to select</p>

          <input
            type="file"
            id="fileInput"
            className="d-none"
            onChange={handleFileSelect}
            ref={fileInputRef}

          />
        </div>

        <div class="selected-files">
          {selectedFiles.map((file, index) => (
            <p key={index}>{file.name}</p>
          ))}
        </div>

        <div class="upload-btns">
          <div class="select-btn">
            <button
              className="btn btn-success"
              onClick={() => fileInputRef.current.click()}
            >
              Select Invoices
            </button>
          </div>
          <div class="upload-btn">
            <button className="btn btn-success" onClick={handleUpload} value={'file'}>
              Upload Invoices
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Upload;
