import React, { useState } from 'react';
import '../NavigationBar/navigationbar.css';

const NavigationBar = () => {
  const [isCollapsed, setIsCollapsed] = useState(false);

  const toggleCollapse = () => {
    setIsCollapsed(!isCollapsed);
  };

  const handleFocus = () => {
    if (isCollapsed) {
      // Move focus to the container when it is opened
      const container = document.getElementById("navbarToggleExternalContent");
      container.focus();
    }
  };

  return (
    <div>
      <div className={`collapse ${isCollapsed ? 'show' : ''}`} id="navbarToggleExternalContent" data-bs-theme="dark">
        <div className="bg-dark p-4">
          <h5 className="text-body-emphasis h4">Collapsed content</h5>
          <span className="text-body-secondary">Toggleable via the navbar brand.</span>
        </div>
      </div>
      <nav className="navbar navbar-dark bg-dark">
        <div className="container-fluid">
          <button className="navbar-toggler" type="button" onClick={() => { toggleCollapse(); handleFocus(); }} aria-expanded={isCollapsed ? 'true' : 'false'} aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
        </div>
      </nav>
    </div>
  );
};

export default NavigationBar;
