import React, { useState, useRef } from "react";
import axios from "axios";
import "./upload.css";
import UploadIcon from "../../assets/uploadicon.svg";

function Upload() {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [uploadStatus, setUploadStatus] = useState(null);
  const fileInputRef = useRef(null);

  const handleFileDrop = (e) => {
    e.preventDefault();
    const files = Array.from(e.dataTransfer.files);
    setSelectedFiles(files);
  };

  const handleFileSelect = (e) => {
    const files = Array.from(e.target.files);
    setSelectedFiles(files);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    selectedFiles.forEach((file) => {
      formData.append("file", file);
    });
  
    try {
      const response = await axios.post("http://127.0.0.1:8000/filestore/upload_invoice/", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      console.log(response.data);
      if (response.status === 200) {
        setUploadStatus({ type: "success", message: "Files uploaded successfully!" });
        setSelectedFiles([]);
      }
    } catch (error) {
      setUploadStatus({ type: "error", message: "Error uploading files. Please try again." });
      console.error("Error uploading files: ", error);
    }
  };

  const handleAreaClick = () => {
    fileInputRef.current.click();
  };

  return (
    <div>
      <div className="container">
        <div className="upload-text">
          <h1>Upload Your Invoices Here...</h1>
        </div>
        <div
          className="drop-area"
          onClick={handleAreaClick}
          onDragOver={(e) => e.preventDefault()}
          onDrop={handleFileDrop}
        >
          <img src={UploadIcon} alt="file icon" className="file-icon" />
          <p className="file-text">Drop files here or click to select</p>

          <input
            type="file"
            id="fileInput"
            className="d-none"
            onChange={handleFileSelect}
            ref={fileInputRef}
            multiple
          />
        </div>

        <div className="selected-files">
          {selectedFiles.map((file, index) => (
            <p key={index}>{file.name}</p>
          ))}
        </div>

        <div className="upload-btns">
          <div className="select-btn">
            <button
              className="btn btn-success"
              onClick={() => fileInputRef.current.click()}
            >
              Select Invoices
            </button>
          </div>
          <div className="upload-btn">
            <button className="btn btn-success" onClick={handleUpload}>
              Upload Invoices
            </button>
          </div>
        </div>

        {uploadStatus && (
          <div className={`alert ${uploadStatus.type === "success" ? "alert-success" : "alert-danger"}`} role="alert">
            {uploadStatus.message}
          </div>
        )}
      </div>
    </div>
  );
}

export default Upload;
